:root {
    // --theme-text-color: #64656a;
    // --theme-background-color: #4b4b4d;
    --primary: #0D0D0D;
    --accent: #6B3BAB;
    --gray-dark: #A0A0A0;
    --gray: #C3C4C4;
    --secondary: #343434;
}

html {
    box-sizing: border-box;
}

*,
*::before,
*::after {
    box-sizing: border-box;
}

html,
body {
    // height: 100%;
}

a {
    text-decoration: none;
    color: inherit;

    &:hover {
        color: inherit;
    }
}

button:focus,
input:focus {
    outline: transparent;
}

ul,
ol,
li,
p,
h1,
h2,
h3,
h4,
h5,
h6 {
    margin: 0;
    padding: 0;
    line-height: 150%;
}

body {
    margin: 0;
    padding: 0;
    position: relative;
    overflow-x: hidden;
    min-width: 320px;
    max-width: 100vw;
    cursor: default;
    font-weight: 400;
    line-height: 140%;
    font-family: "Libre Franklin", sans-serif;
    font-size: 18px;
    line-height: 180%;
    background: #EEEEEE;
    // transition: 0.5s;
    // padding-top: 90px;

    &.p0 {
        padding-top: 0;
    }

    &.loadpage {
        // padding-top: 0;
    }
}

button {
    outline: none;
    padding: 0;
    background: transparent;
    border: none;
}

.containersect {
    padding-right: 128px;
    padding-left: 128px;
    width: 100%;
}

.text-center {
    text-align: center;
}

.text-end {
    text-align: right;
}

main {
    // padding-top: 90px;
}

input::-webkit-outer-spin-button,
input::-webkit-inner-spin-button {
    -webkit-appearance: none;
    margin: 0;
}

input[type=number] {
    -moz-appearance: textfield;
}

img {
    max-width: 100%;
}

@media(min-width:1440px) {
    .container {
        max-width: 1208px;
    }
}



@mixin h1 {
    font-size: 56px;
    line-height: 130%;
    color: #000;
    font-weight: 600;
}

@mixin h2 {
    font-size: 40px;
    line-height: 130%;
    color: #000;
    font-weight: 800;
}

@mixin h3 {
    font-size: 28px;
    line-height: 160%;
    color: #000;
    font-weight: 400;
}

@mixin h4 {
    font-size: 24px;
    line-height: 160%;
    color: #000;
    font-weight: 400;
}

@mixin h5 {
    font-size: 18px;
    line-height: 100%;
    color: #000;
    font-weight: 500;
}

@mixin h6 {
    font-size: 16px;
    color: #000;
    font-weight: 500;
}

@mixin body {
    font-size: 18px;
    line-height: 180%;
    color: #000;
}

@mixin body1 {
    font-size: 16px;
    line-height: 170%;
    color: #000;
}

@mixin body2 {
    font-size: 14px;
    line-height: 130%;
    color: #000;
}

@mixin body3 {
    font-size: 12px;
    font-weight: 300;
    line-height: 130%;
    color: #000;
}

@mixin button {
    font-size: 16px;
    color: #000;
    font-weight: 500;
    line-height: 100%;
}



h1 {}

h2 {}

h3 {}

h4 {}

h5 {}

h6 {}


@media(max-width:1499.8px) {
    .container {
        max-width: 100%;
        padding-left: 75px;
        padding-right: 75px;
    }
}

@media(max-width:991.8px) {
    .container {
        padding-left: 25px;
        padding-right: 25px;
    }
}

@media(max-width:575.8px) {
    .container,
    .containersect {
        padding-left: 12px;
        padding-right: 12px;
    }
}
