commits.content:
header.header,
.pagesideline.pagesideline__right,
.pagesideline.pagesideline__left,
.scrollnext {
    transition: 0.5s;
}

header.header {
    position: fixed;
    left: 0;
    top: 0;
    width: 100%;
    background: #EEEEEE;
    z-index: 12;
    display: flex;
    align-items: center;
    justify-content: center;
    height: 70px;

    nav {
        ul {
            display: flex;
            align-items: center;
            justify-content: center;
            list-style-type: none;

            li {
                margin: 0 20px;

                a {
                    text-transform: uppercase;
                    color: var(--primary);
                    font-size: 16px;
                    font-weight: 500;
                    transition: 0.5s;
                    position: relative;

                    &::after {
                        content: '';
                        display: block;
                        height: 1px;
                        width: 0;
                        background: var(--accent);
                        position: absolute;
                        left: 0;
                        right: 0;
                        margin: 0 auto;
                        bottom: 0;
                        transition: 0.5s;
                    }

                    &:hover {
                        color: var(--accent);

                        &::after {
                            width: 100%;
                        }
                    }
                }
            }
        }
    }
}

.burger,
.header__logo,
.header__mobelems,
.header__bottombox {
    display: none;
}

@media(max-width:1439.8px) {
    header.header {
        height: 75px;
    }

    header.header nav ul li a {
        font-size: 15px;
    }
}

@media(max-width:991.8px) {
    header.header {
        background: #fff;
    }

    header.header .headerwrapper {
        display: flex;
        flex-direction: column;
        justify-content: space-between;
        overflow: auto;
        transition: 0.3s;
        transform: translateX(100%);
        position: fixed;
        left: 0;
        top: 75px;
        width: 100%;
        height: calc(100vh - 75px);
        background: #f7f7f8;
        padding: 40px 0;

        &.show {
            transform: translateX(0);
        }

        li {
            opacity: 0;
            transform: translateX(10px);
            transition: 0.3s;
        }

    }



    button.burger {
        display: block;
        position: absolute;
        top: 50%;
        transform: translateY(-50%);
        right: 25px;
        width: 27px;
        height: 18px;
        border: none;
        background: transparent;
        z-index: 15;
        cursor: pointer;
        padding: 0;
        margin: 0 0 0 auto;

        &:focus {
            outline: none;
        }

        &:after,
        &:before,
        span {
            width: 100%;
            height: 2px;
            border-radius: 100px;
            position: absolute;
            display: block;
            background: #000;
            right: 0;
        }

        &:after,
        &:before {
            content: "";
            transition: 0.3s width 0.4s;
        }

        &:after {
            top: 0px;
            left: 0px;
            margin-top: 0px;
        }

        &:before {
            bottom: 0px;
            right: 0px;
            margin-bottom: 0px;
        }

        span {
            top: 50%;
            margin-top: -1px;
            transition: 0.3s;

            &:before {
                content: "";
                background: #000;
                width: 100%;
                height: 2px;
                border-radius: 100px;
                position: absolute;
                left: 0px;
                transition: 0.3s;
            }
        }

        &.on {

            &:after {
                transition: all 0.2s;
                width: 0%;
                left: 0px;
                background: #000;
            }

            &:before {
                transition: all 0.2s;
                width: 0%;
                right: 0px;
                background: #000;
            }

            span {
                transform: rotate(45deg);
                transition: 0.3s transform 0.4s;
                background: #000;

                &:before {
                    transform: rotate(-90deg);
                    transition: 0.3s transform 0.4s;
                    background: #000;
                }
            }
        }
    }

    .header__logo {
        display: inline-block;

        img {
            width: 120px;
            vertical-align: top;
        }
    }

    .header__mobelems {
        display: flex;
        align-items: center;
        height: 75px;
    }

    header.header nav ul {
        display: block;

        li {
            margin-bottom: 15px;

            a {
                font-size: 18px;
            }
        }
    }

    .header__bottombox {
        display: block;
        text-align: center;

        .languagelist {
            margin-bottom: 20px;

            li {
                display: inline-block;
                vertical-align: middle;

                a {
                    border-bottom: none;
                    border-right: 1px solid #3D3743;
                    font-size: 16px;
                    padding: 0px 15px;
                }

                &:last-child {
                    a {
                        border-right: none;
                    }
                }
            }
        }

        .leftsoclist {
            li {
                display: inline-block;
                vertical-align: middle;
                margin: 0 5px;

                a {
                    img {
                        width: 30px;
                    }
                }
            }
        }

    }






}
