.leftlogo {
    transition: 0.3s;
    filter: brightness(0);
    margin-bottom: 100px;
    display: inline-block;

    &:hover {
        filter: brightness(1);
    }
}

.leftsoclist {
    li {
        margin-bottom: 14px;

        &:last-child {
            margin-bottom: 0;
        }

        a {
            transition: 0.3s;
            filter: brightness(0);

            img {
                width: 24px;
                vertical-align: top;
            }

            &:hover {
                filter: brightness(1);
            }
        }
    }
}

.languagelist {
    list-style-type: none;

    li {

        a {
            text-transform: uppercase;
            color: var(--primary);
            font-size: 16px;
            font-weight: 500;
            transition: 0.3s;
            padding: 4px 10px;
            border-bottom: 1px solid #3D3743;
            display: inline-block;

            &:hover {
                color: var(--accent);
            }
        }

        &:last-child {
            a {
                border-bottom: none;
            }
        }
    }
}

.welcomesection {
    position: relative;
    padding-top: 90px;

    .containersect {
        transition: 0.5s;
    }

    .imgwrap {
        position: relative;
        border-radius: 16px;
        overflow: hidden;
        // height: 600px;
        height: calc(100vh - 150px);
        transition: 0.5s;
        // max-height: calc(100vh - 110px);

        img {
            width: 100%;
            height: 100%;
            object-fit: cover;
            object-position: 50% 50%;
            vertical-align: top;
        }
    }

    .welcomesection__scrollarr {
        position: absolute;
        bottom: 48px;
        z-index: 2;
        left: 50%;
        margin-left: -24px;
        display: inline-block;

        img {
            width: 70px;
            vertical-align: top;
        }
    }
}


// filter
.filter-sectionwrap {
    margin-top: 80px;
}

.filter-section {
    background: #fff;
    border-radius: 16px;
    position: relative;
}

.filter-section__header {
    ul {
        display: flex;
        align-items: center;
        list-style-type: none;

        li {
            position: relative;

            &::after {
                content: '';
                width: 1px;
                height: 24px;
                background: #EEEEEE;
                border-radius: 8px;
                position: absolute;
                right: 0;
                top: 50%;
                transform: translateY(-50%)
            }

            &:last-child {
                &::after {
                    display: none;
                }
            }

            button {
                @include h6;
                color: var(--gray-dark);
                height: 59px;
                min-width: 120px;
                padding: 5px 10px;
                cursor: pointer;
                transition: 0.3s;

                &:hover {
                    color: var(--accent)
                }

                &.active {
                    color: var(--accent);
                }
            }


        }
    }
}

.filter-section__body {
    padding: 16px 0;
}

.filter__searchboxwrap {
    padding: 0 16px;
}

.filter__searchbox {
    position: relative;
    margin-bottom: 30px;

    input {
        width: 100%;
        height: 56px;
        border: 1px solid #C3C4C4;
        padding-left: 48px;
        padding-right: 15px;
        border-radius: 12px;
        font-size: 16px;
        font-weight: 500;
        color: #0D0D0D;
        font-family: "Libre Franklin", sans-serif;
        transition: 0.3s;

        &::placeholder {
            color: #0D0D0D;
        }

        &:focus,
        &:not(:placeholder-shown) {
            border-color: var(--primary);
        }
    }

    .search-ic {
        position: absolute;
        width: 24px;
        left: 16px;
        top: 50%;
        transform: translateY(-50%);
    }
}

.fscontent {
    display: flex;
    flex-wrap: wrap;
    align-items: center;
}

.fscontent__type {
    width: 100%;
    max-width: calc((100% - 141px) / 4);
    flex: 0 0 calc((100% - 141px) / 4);
    padding-left: 16px;
    padding-right: 5px;
    position: relative;

    &::after {
        content: "";
        width: 1px;
        height: 24px;
        background: #EEEEEE;
        border-radius: 8px;
        position: absolute;
        right: 0;
        top: 50%;
        transform: translateY(-50%);
    }
}

.fscontent__bb {
    width: 100%;
    max-width: calc((100% - 141px) / 4);
    flex: 0 0 calc((100% - 141px) / 4);
    padding-left: 16px;
    padding-right: 5px;
    position: relative;

    &::after {
        content: "";
        width: 1px;
        height: 24px;
        background: #EEEEEE;
        border-radius: 8px;
        position: absolute;
        right: 0;
        top: 50%;
        transform: translateY(-50%);
    }
}

.fscontent__price {
    width: 100%;
    max-width: calc((100% - 141px) / 4);
    flex: 0 0 calc((100% - 141px) / 4);
    padding-left: 16px;
    padding-right: 5px;
    position: relative;

    &::after {
        content: "";
        width: 1px;
        height: 24px;
        background: #EEEEEE;
        border-radius: 8px;
        position: absolute;
        right: 0;
        top: 50%;
        transform: translateY(-50%);
    }
}

.fscontent__loc {
    width: 100%;
    max-width: calc((100% - 141px) / 4);
    flex: 0 0 calc((100% - 141px) / 4);
    padding-left: 16px;
    padding-right: 5px;
    position: relative;

    p {
        position: relative;
        padding-left: 32px;
        color: var(--primary);
        font-size: 16px;
        font-weight: 500;
        height: 28px;
        display: flex;
        align-items: flex-end;

        img {
            position: absolute;
            left: 0;
            top: 3px;
            width: 24px;
        }
    }
}

.fscontent__btn {
    width: 100%;
    max-width: 141px;
    flex: 0 0 141px;
    padding-left: 12px;
    padding-right: 12px;

    input {
        width: 100%;
        height: 50px;
        display: flex;
        align-items: center;
        justify-content: center;
        border-radius: 16px;
        background: var(--accent);
        transition: 0.5s;
        color: #fff;
        font-size: 16px;
        font-weight: 500;
        font-family: "Libre Franklin", sans-serif;
        border: none;
        cursor: pointer;

        &:hover {
            box-shadow: 1px 2px 9px rgba(#4900AA, 0.7);
        }
    }
}

.styledselect__wrap {
    .select2 {
        width: 100% !important;
    }

    .select2-container--default .select2-selection--single {
        border: none;
        border-radius: 0;
    }

    .select2-container--default .select2-selection--single .select2-selection__placeholder,
    .select2-container--default .select2-selection--single .select2-selection__rendered {
        color: var(--primary);
        font-size: 16px;
        font-weight: 500;
        font-family: "Libre Franklin", sans-serif;
    }

    .select2-container--default .select2-selection--single .select2-selection__arrow b {
        display: none;
    }

    .select2-container--default .select2-selection--single .select2-selection__arrow {
        width: 24px;
        height: 25px;
        background: url('../img/general/select-arr.svg') no-repeat center / 100%;
        transition: 0.3s;
    }

    .select2-container--default.select2-container--open .select2-selection--single .select2-selection__arrow {
        transform: rotate(-180deg);
    }



}

.select2-dropdown {
    border: none;
    box-shadow: 0 7px 12px rgba(#000, 0.3);
    padding: 8px 4px;
    border-radius: 0 0 10px 10px;
}

.select2-results__option {
    font-size: 14px;
    color: var(--primary);
    line-height: 130%;
    border-radius: 8px;
    padding: 8px;
}

.select2-container--default .select2-results__option--highlighted.select2-results__option--selectable {
    background: #EEEEEE;
    color: var(--primary);
}

.select2-container--default .select2-results>.select2-results__options {
    &::-webkit-scrollbar {
        width: 6px;
    }

    &::-webkit-scrollbar-track {
        // box-shadow: inset 0 0 6px rgba(0, 0, 0, 0.3);
    }

    &::-webkit-scrollbar-thumb {
        background-color: rgb(219, 219, 219);
        border-radius: 8px;
    }
}

.ddmenu {
    background: #fff;
    border-radius: 0 0 10px 10px;
    position: absolute;
    top: 32px;
    left: 0;
    box-shadow: 0 7px 12px rgba(#000, 0.3);
    width: 280px;
    padding: 16px 12px 12px 12px;
    display: none;
    z-index: 3;

    &.price {
        width: 390px;
        display: flex;
        flex-wrap: wrap;
        margin-left: -4px;
        margin-right: -4px;

        .ddprce-col {
            width: 100%;
            max-width: 50%;
            flex: 0 0 50%;
            padding: 0 4px
        }
    }
}

.selecteltext {
    @include h6;
    position: relative;
    color: var(--primary);
    height: 28px;
    display: flex;
    align-items: flex-end;
    width: 100%;
    max-width: 100%;
    flex: 0 0 100%;
    cursor: pointer;
    padding-right: 20px;

    &::after {
        content: '';
        width: 24px;
        height: 25px;
        background: url(../img/general/select-arr.svg) no-repeat center / 100%;
        -webkit-transition: 0.3s;
        transition: 0.3s;
        right: 0;
        position: absolute;
    }

    &.active {
        &::after {
            transform: rotate(-180deg);
        }
    }

    span {
        text-transform: uppercase;
        color: var(--gray-dark);
        display: inline-block;
        margin-right: 4px;
    }
}

.bb-labels {
    display: flex;
    flex-wrap: wrap;
    padding: 0 6px;
}

.bb-label {
    margin-right: 6px;
    margin-bottom: 8px;

    input {
        display: none;

        &:checked+label {
            background: #EEEEEE
        }
    }

    label {
        @include body2;
        color: var(--primary);
        padding: 7px 15px;
        border-radius: 8px;
        border: 1px solid #EEEEEE;
        cursor: pointer;
    }
}

.labksbox {
    .title {
        padding: 6px;
        margin-bottom: 4px;
        @include body1;
        color: var(--primary);
    }
}

.ddprce-col {
    .title {
        padding: 6px;
        margin-bottom: 16px;
        font-size: 16px;
        line-height: 170%;
        color: var(--primary);
    }

    .inputfield {
        position: relative;
        margin-bottom: 16px;

        input {
            height: 48px;
            border: 1px solid #A0A0A0;
            border-radius: 8px;
            padding-left: 50px;
            color: #3D3743;
            font-size: 16px;
            font-family: "Libre Franklin", sans-serif;
            width: 100%;
            transition: 0.3s;

            &:focus {
                border: 1px solid #0D0D0D;
            }
        }

        span {
            @include body1;
            position: absolute;
            text-transform: uppercase;
            left: 8px;
            top: 11px;
            color: var(--gray-dark);
        }
    }

    button {
        font-family: "Libre Franklin", sans-serif;
        height: 50px;
        border-radius: 16px;
        width: 100%;
        font-size: 16px;
        font-weight: 500;
        cursor: pointer;

        &.reset {
            color: var(--primary);
            border: 1px solid var(--primary);
        }

        &.apply {
            color: #fff;
            background: var(--accent);
        }
    }
}

.fscontent__price {
    .ddmenu {
        display: none;

        &.show {
            display: flex;
        }
    }
}

.filter-sectionwrap.frstsection {
    padding-top: 90px;
}

// filter end

// popular property
.objects-section {
    padding: 48px 0 0 0;

    .titlesect {
        @include h1;
        margin-bottom: 32px;
        color: var(--primary);
    }
}

.objects-section__sortrow {
    display: flex;
    align-items: center;
    margin-bottom: 32px;

    p {
        @include h6;
        width: 100%;
        max-width: calc(100% - 150px);
        flex: 0 0 calc(100% - 150px);
        margin: 0;
        color: var(--primary);
    }

    .poppropselect {
        width: 100%;
        max-width: 150px;
        flex: 0 0 150px;

        .select2-container {
            width: 100% !important;
        }

        .select2-container--default .select2-selection--single {
            border: none;
            background: transparent;
            border-radius: 0;
        }

        .select2-container--default .select2-selection--single .select2-selection__rendered {
            @include h6;
            color: var(--primary);
        }

        .select2-container--default .select2-selection--single .select2-selection__arrow b {
            display: none;
        }

        .select2-container--default .select2-selection--single .select2-selection__arrow {
            width: 24px;
            height: 25px;
            background: url('../img/general/select-arr.svg') no-repeat center / 100%;
            transition: 0.3s;
        }

        .select2-container--default.select2-container--open .select2-selection--single .select2-selection__arrow {
            transform: rotate(-180deg);
        }
    }
}

.objectcard {
    aspect-ratio: 1 / 1;
    position: relative;
    border-radius: 16px;
    overflow: hidden;
    display: flex;
    align-items: flex-end;
    justify-content: center;
    padding-bottom: 9px;
    margin-bottom: 16px;

    img {
        width: 100%;
        height: 100%;
        object-fit: cover;
        object-position: 50% 50%;
        position: absolute;
        left: 0;
        top: 0;
        transition: 0.5s;
        transform: scale(1.01);
    }

    &:hover {
        img {
            transform: scale(1.1);
        }
    }

    .objectcard__info {
        border-radius: 12px;
        padding: 12px;
        background: rgba(#3D3743, 0.96);
        z-index: 2;
        width: calc(100% - 16px);

    }
}

.objectcard__info-header {
    margin-bottom: 12px;
    position: relative;

    &::after {
        content: '';
        display: inline-block;
        width: 28px;
        height: 28px;
        background: url('../img/main/obj-arrow.svg') no-repeat center / 100%;
        position: absolute;
        right: 0;
        top: 3px;

    }

    .title {
        @include h6;
        margin: 0;
        color: #fff
    }

    p {
        @include body3;
        color: #fff;
        margin: 0;

    }
}

.objectcard__info-body {
    display: flex;
    flex-wrap: wrap;
    align-items: center;

    .descrbox {
        position: relative;
        width: 100%;
        text-align: center;
        padding: 0 3px;


        &::after {
            content: '';
            display: inline-block;
            border-radius: 8px;
            background: var(--gray);
            width: 1px;
            height: 24px;
            position: absolute;
            right: 0;
            top: 50%;
            transform: translateY(-50%);
        }

        &:last-child {
            &::after {
                display: none;
            }
        }

        p {
            @include body3;
            color: var(--gray);

            span {
                display: block;
                color: #fff;
                margin-bottom: 2px;
            }
        }

        &.descrbox__beds {
            max-width: 20%;
            flex: 0 0 20%;
        }

        &.descrbox__baths {
            max-width: 23%;
            flex: 0 0 23%;
        }

        &.descrbox__price {
            max-width: 23%;
            flex: 0 0 23%;
        }

        &.descrbox__type {
            max-width: 34%;
            flex: 0 0 34%;
            text-align: left;
            padding-left: 10px;

            p {
                color: #fff;
            }
        }

    }
}

.loadmore {
    display: inline-block;
    margin-top: 50px;
    color: var(--primary);
    font-size: 16px;
    font-family: "Libre Franklin", sans-serif;
    cursor: pointer;
    position: relative;
    line-height: 100%;

    &::after {
        content: '';
        display: block;
        height: 1px;
        width: 0;
        transition: 0.5s;
        background: var(--primary);
        position: absolute;
        left: 0;
        bottom: -2px;
    }

    &:hover {
        &::after {
            width: 100%;
        }
    }
}

// CONTACT SECTION
.contactsection {
    padding: 98px 0 80px;
}

.contactsection__left {
    .title {
        @include h1;
        color: var(--primary);
        margin-bottom: 16px;
        max-width: 100%;
    }

    p {
        max-width: 400px;
        @include body1;
        color: var(--primary);
        margin-bottom: 48px;
    }

    ul {
        list-style-type: none;
        margin: 0;
        padding: 0;

        li {
            display: flex;
            flex-wrap: wrap;
            align-items: center;
            margin-bottom: 32px;

            .imgwrap {
                width: 100%;
                max-width: 70px;
                flex: 0 0 70px;

                img {
                    width: 100%;
                    vertical-align: top;
                }
            }

            .rightinfo {
                width: 100%;
                max-width: calc(100% - 70px);
                flex: 0 0 calc(100% - 70px);
                padding-left: 32px;

                p {
                    margin-bottom: 8px;
                    @include body2;
                    color: var(--primary);
                }

                a {
                    @include body1;
                    color: var(--primary);
                    transition: 0.3s;

                    &:hover {
                        color: var(--accent);
                    }
                }
            }
        }
    }
}

.contactsection__right {
    padding-left: 30px;
}

.formdark {
    background: var(--secondary);
    border-radius: 22px;
    padding: 40px 32px;

    .inputfield {
        margin-bottom: 24px;

        label {
            @include h5;
            display: block;
            margin-bottom: 8px;
            color: var(--gray);
        }

        input,
        textarea {
            width: 100%;
            height: 54px;
            border-radius: 12px;
            background: #48484A;
            padding-left: 12px;
            color: #EEEEEE;
            border: none;
            font-size: 16px;
            font-family: "Libre Franklin", sans-serif;
            transition: 0.3s;

            &:focus {
                background: #3D3743;
            }

            &:not(:placeholder-shown) {
                background: #3D3743;
            }

            &::placeholder {
                color: var(--gray);
            }
        }

        textarea {
            resize: none;
            height: 120px;
            padding-top: 15px;
            outline: none;
            vertical-align: top;
        }
    }

    .submbtn {
        width: 100%;
        background: var(--accent);
        height: 50px;
        border-radius: 16px;
        border: none;
        transition: 0.3s;
        cursor: pointer;
        color: #fff;
        font-size: 16px;
        font-weight: 500;
        font-family: "Libre Franklin", sans-serif;

        &:hover {
            background: #fff;
            color: var(--accent);
        }
    }
}

.objectcard__info-header {
    padding-right: 30px;
}

@media(max-width:1439.8px) {
    .pagesideline {
        width: 75px;
    }

    .containersect {
        padding-left: 75px;
        padding-right: 75px;
    }

    .welcomesection {
        padding-top: 75px;
    }

    .welcomesection .imgwrap {
        height: calc(100vh - 120px);
    }

    .languagelist li a {
        padding: 4px 6px;
        font-size: 15px;
    }

    .welcomesection .welcomesection__scrollarr img {
        width: 50px;
    }

    .objects-section .titlesect {
        margin-bottom: 20px;
    }

    .objectcard__info-header {
        margin-bottom: 5px;
    }

    .objectcard .objectcard__info {
        border-radius: 8px;
        padding: 8px;
    }

    .filter-section__header ul li button {
        height: 50px;
    }

    .filter-section__body {
        padding-top: 3px;
    }

    .filter__searchbox {
        margin-bottom: 20px;
    }

    .loadmore {
        margin-top: 30px;
    }

    .contactsection__left p {
        margin-bottom: 30px;
    }

    .contactsection__left ul li .imgwrap {
        max-width: 50px;
        flex: 0 0 50px;
    }

    .contactsection__left ul li .rightinfo {
        max-width: calc(100% - 50px);
        flex: 0 0 calc(100% - 50px);
        padding-left: 20px;
    }

    .contactsection__left ul li .rightinfo p {
        margin-bottom: 5px;
    }

    .contactsection__left ul li {
        margin-bottom: 20px;
    }

    .objectcard__info-body .descrbox.descrbox__type {
        padding-left: 5px;
    }

    .formdark .inputfield {
        margin-bottom: 16px;

        input {
            height: 48px;
            border-radius: 8px;
        }
    }

    .formdark {
        border-radius: 15px;
        padding: 30px;
    }

    .contactsection {
        padding-top: 70px;
    }


    .objects-section .titlesect,
    .contactsection__left .title {
        font-size: 40px;
    }

    .formdark .inputfield label {
        font-size: 16px;
    }

    .objectcard__info-header .title {
        font-size: 14px;
    }

}

@media(max-width:1199.8px) {

    .welcomesection .imgwrap {
        height: auto;
    }

    .welcomesection .welcomesection__scrollarr {
